mapper.select:
SELECT EXISTS(
    SELECT NICKNAME
    FROM USER_INFO
    WHERE NICKNAME = #{nickname}
)

SELECT UID, NICKNAME, INTRODUCE
FROM USER_INFO
WHERE UID = #{uid}

SELECT UID, (SELECT COUNT(B.UID) FROM FEED B WHERE B.UID = A.UID) AS FEED_COUNT FROM USER_INFO A WHERE UID NOT IN (
#{item}
) ORDER BY FEED_COUNT DESC
LIMIT #{needSize, jdbcType=INTEGER}
